public class Main {    public int bubbleSort (int[] a, int n) {        int tmp, sw = 0;        boolean flag = true;        for(int i = 0 ; flag ; i++) {            flag = false;            for(int j = n-1 ; j >= i+1 ; j--) {                if (a[j] < a[j-1]) {                    tmp = a[j];                    a[j] = a[j-1];                    a[j-1] = tmp;                    flag = true;                    sw++;                }            }        }		return sw;    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        Main main = new Main();        int n, sw = 0;        n = sc.nextInt();        int[] a = new int[n];        for (int i = 0 ; i < n ; i++) {            a[i] = sc.nextInt();        }        sw = main.bubbleSort(a, n);        for (int j = 0 ; j < n ; j++) {            if(j != 0) System.out.print(" ");            System.out.print(a[j]);        }        System.out.println();        System.out.println(sw);    }}